import {REGISTER} from './types';


export const RegisterUser = ({ data }) => {
    return (dispatch) => {
      return axios.post('http://localhost:4000/backend/add', {data})
        .then(response => {
          dispatch(RegisterAction(response.data))
        })
        .catch(error => {
          throw(error);
        });
    };
  };

const action =(type,payload) =>{
    return{type,payload}
}
export const RegisterAction =(data)=>{return (dispatch) => {}
    return axios.post('http://localhost:4000/backend/add', {data})
      .then(response => {
