//******************************************
//****** APP.JS - Main App Entry Point *****
//******************************************

import React, { useState } from 'react';
import {Provider} from 'react-redux';
import PersistedStore from '../src/store/store/PersistedStore'
import Home from "./components/Home";
import AddItem from './components/AddItem';
import store from './store/store/index';


function App(){

  const [data,setData] = useState([])

  const downnload =()=>{
    var data = [
      ['name1', 'city1', 'some other info'],
      ['name2', 'city2', 'more info']
    ];

    // Building the CSV from the Data two-dimensional array
    // Each column is separated by ";" and new line "\n" for next row
    var csvContent = '';
    data.forEach(function(infoArray, index) {
      const dataString = infoArray.join(';');
      csvContent += index < data.length ? dataString + '\n' : dataString;
    });

    // The download function takes a CSV string, the filename and mimeType as parameters
    // Scroll/look down at the bottom of this snippet to see how download is called
    var download = function(content, fileName, mimeType) {
      var a = document.createElement('a');
      mimeType = mimeType || 'application/octet-stream';

      if (navigator.msSaveBlob) { // IE10
        navigator.msSaveBlob(new Blob([content], {
          type: mimeType
        }), fileName);
      } else if (URL && 'download' in a) { //html5 A[download]
        a.href = URL.createObjectURL(new Blob([content], {
          type: mimeType
        }));
        a.setAttribute('download', fileName);
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
      } else {
        window.location.href = 'data:application/octet-stream,' + encodeURIComponent(content); // only this mime type is supported
      }
    }

    download(csvContent, 'dowload.csv', 'text/csv;encoding:utf-8');

  }

    console.log(store.getState());
    return (
      <Provider store={PersistedStore.getDefaultStore().store}>
        <div>
          <button onClick = {downnload}>Download</button>
        </div>
      <AddItem />
      <Home />
      </Provider>
    )
}

export default App;
