// import axios from 'axios'
import {ADDITEM,DELETE} from './types';

const action =(type,payload) =>{
    return{type,payload}
}
export const AddItemAction =(item,cost,quantity,id)=>
    action(ADDITEM,
    {
    item,
    cost,
    id
}) 
export const deleteAction = id => action(DELETE, id)
