import React, {useState} from 'react'
import {useDispatch,useSelector} from 'react-redux';
import {Link} from 'react-router-dom';
import {RegisterAction} from '../store/actions/index';
import store from '../store/store/index';



const Register = props => {


const dispatch = useDispatch();

  const [firstName, setFirstName] = useState("");
  const [lastName,setLastName] = useState("");
  const [email,setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mobileNumber,setMobileNumber]=useState("");
//   const [response, setResponse] = useState("");

const RegisterData = useSelector(state=>state.RegisterReducer[0])
console.log(RegisterData)


  const handleLogin = (evt) => {
      evt.preventDefault();
      console.log(firstName);
      console.log(lastName);
      console.log(email);
      console.log(password);
      console.log(mobileNumber);
      const data ={
          firstName:firstName,
          lastName:lastName,
          email:email,
          password:password,
          mobileNumber:mobileNumber
      }

    dispatch(RegisterAction(data));

    fetch('http://localhost:4000/backend/add', {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({data})
    }).then(function(response) {
      if(response.status >= 400) {
        throw new Error("Bad response from server");
      }
      return response.json();
    }).then((data) =>{
      console.log(data);     
      if(data){

         this.props.history.push('/');
      }   
    }).catch(function(err) {
      console.log(err)
    });

      setFirstName('');
      setLastName('')
      setPassword('');
      setEmail('');
      setPassword('');
      setMobileNumber('');

  }
   store.subscribe(()=>{console.log(store.getState())})
  return(
    <div>
    {/* <Navbar /> */}
    <center>
    <div className="loginMain">
      <div className="loginLogo">
        <div className="loginContent">

        </div>
      </div>
      <div className="registerContent">

      </div>
      <div className="col-6 inputFields">
          <center>
        <form onSubmit={handleLogin}>
            <div className="">
              <label>
              <input className="form-control" type="text"
              value={firstName}
              onChange={e => setFirstName(e.target.value)}
              placeholder="FirstName" required/>
              </label>
            </div>

            <div className="">
              <label>
              <input className="form-control" type="text"
              value={lastName}
              onChange={e => setLastName(e.target.value)}
              placeholder="LastName" required/>
              </label>
            </div>

            <div className="">
              <label>
              <input className="form-control" type="email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              placeholder="Email" required/>
              </label>
            </div>

            <div className="">
              <label>
              <input className="form-control" type="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              placeholder="Password" required/>
              </label>
            </div>

            <div className="">
              <label>
              <input className="form-control" type="text"
              value={mobileNumber}
              onChange={e => setMobileNumber(e.target.value)}
              placeholder="mobileNumber" required/>
              </label>
            </div>


            <div className="inputFields">
            <center>  <input className="btn-sm login" type="submit" value="Register" /></center>
            </div>
            <Link to="/"> login</Link>
        </form>
        </center>
      </div>
    </div></center>

    </div>
  );
}
export default Register;
