//******************************************
//****** HOME.JS - Item List In Table *****
//******************************************

import React from 'react';
import {useSelector,useDispatch} from 'react-redux';
import {deleteAction} from '../store/actions/index';
import Table from 'react-bootstrap/Table';
import './Home.css';

const Home = props =>{


    const AddItemReducer = useSelector(state=>state.AddItemReducer)
    console.log(AddItemReducer)
    const downnload =()=>{
        var data  = AddItemReducer

        // Building the CSV from the Data two-dimensional array
        // Each column is separated by ";" and new line "\n" for next row
        var csvContent = '';
        data.forEach(function(infoArray, index) {
          const dataString = infoArray.join(';');
          csvContent += index < data.length ? dataString + '\n' : dataString;
        });

        // The download function takes a CSV string, the filename and mimeType as parameters
        // Scroll/look down at the bottom of this snippet to see how download is called
        var download = function(content, fileName, mimeType) {
          var a = document.createElement('a');
          mimeType = mimeType || 'application/octet-stream';

          if (navigator.msSaveBlob) { // IE10
            navigator.msSaveBlob(new Blob([content], {
              type: mimeType
            }), fileName);
          } else if (URL && 'download' in a) { //html5 A[download]
            a.href = URL.createObjectURL(new Blob([content], {
              type: mimeType
            }));
            a.setAttribute('download', fileName);
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
          } else {
            window.location.href = 'data:application/octet-stream,' + encodeURIComponent(content); // only this mime type is supported
          }
        }

        download(csvContent, 'dowload.csv', 'text/csv;encoding:utf-8');

      }


    const AddItemReducerDisplay = AddItemReducer
    var dispatch = useDispatch();

    const onClickDeleteHire = (e,id) => 
    {
      dispatch(deleteAction({ id: id }))
    }


        return(
            <div>
                <div><button onClick={downnload}>Download</button></div>
                <Table>
                    <tbody>
                       <tr><td>Id</td>
                        <td>WorkItem</td>
                        <td>Due Date</td>
                        <td>No Of Resources Needed</td>
                        <td>Actions</td>
                        </tr>
                        {AddItemReducerDisplay.map(data => (
                        <tr key={data.id}>
                             <td>{data.id}</td>
                             <td>{data.item}</td>
                             <td>{data.date}</td>
                             <td>{data.resources}</td>
                             <td><button onClick={() => {
                             onClickDeleteHire(this,data.id)
                             }}>DELETE</button></td>
                            </tr>
                    ))}
                    </tbody>
                </Table>
            </div>
        )  
}

function convertToCSV(objArray) {
    var array = typeof objArray != 'object' ? JSON.parse(objArray) : objArray;
    var str = '';

    for (var i = 0; i < array.length; i++) {
        var line = '';
        for (var index in array[i]) {
            if (line != '') line += ','

            line += array[i][index];
        }

        str += line + '\r\n';
    }

    return str;
}

function exportCSVFile(headers, items, fileTitle) {
    if (headers) {
        items.unshift(headers);
    }

    // Convert Object to JSON
    var jsonObject = JSON.stringify(items);

    var csv = this.convertToCSV(jsonObject);

    var exportedFilenmae = fileTitle + '.csv' || 'export.csv';

    var blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
    if (navigator.msSaveBlob) { // IE 10+
        navigator.msSaveBlob(blob, exportedFilenmae);
    } else {
        var link = document.createElement("a");
        if (link.download !== undefined) { // feature detection
            // Browsers that support HTML5 download attribute
            var url = URL.createObjectURL(blob);
            link.setAttribute("href", url);
            link.setAttribute("download", exportedFilenmae);
            link.style.visibility = 'hidden';
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
        }
    }
}

var headers = {
    model: 'Phone Model'.replace(/,/g, ''), // remove commas to avoid errors
    chargers: "Chargers",
    cases: "Cases",
    earphones: "Earphones"
};

 const itemsNotFormatted = [
    {
        model: 'Samsung S7',
        chargers: '55',
        cases: '56',
        earphones: '57',
        scratched: '2'
    },
    {
        model: 'Pixel XL',
        chargers: '77',
        cases: '78',
        earphones: '79',
        scratched: '4'
    },
    {
        model: 'iPhone 7',
        chargers: '88',
        cases: '89',
        earphones: '90',
        scratched: '6'
    }
];

var itemsFormatted = [];

// format the data
itemsNotFormatted.forEach((item) => {
    itemsFormatted.push({
        model: item.model.replace(/,/g, ''), // remove commas to avoid errors,
        chargers: item.chargers,
        cases: item.cases,
        earphones: item.earphones
    });
});

var fileTitle = 'orders'; // or 'my-unique-title'

exportCSVFile(headers, itemsFormatted, fileTitle); // call the exportCSVFile() function to process the JSON and trigger the download

export default Home
