//******************************************
//****** HOME.JS - Item List In Table *****
//******************************************

import React from 'react';
import {useSelector,useDispatch} from 'react-redux';
import {deleteAction} from '../store/actions/index';
import Table from 'react-bootstrap/Table';
import './Home.css';

const Home = props =>{


    const AddItemReducer = useSelector(state=>state.AddItemReducer)
    console.log(AddItemReducer)
    const AddItemReducerDisplay = AddItemReducer
    var dispatch = useDispatch();

    const onClickDeleteHire = (e,id) => 
    {
      dispatch(deleteAction({ id: id }))
    }

    const Download = () =>{
        var data = AddItemReducer;
        var csvContent = "data:text/csv;charset=utf-8,";

            for (var key in data) {
              if (data.hasOwnProperty(key)) {
                data[key].unshift([key,""]);
                for(var index=0;index<data[key].length;index++){
                    var elementArray = data[key][index];
                    data[key][index] = elementArray.join(",");
                }
                csvContent += data[key].join("\n");
              }
              csvContent += "\n";
            }
        var encodedUri = encodeURI(csvContent);
        var link = document.createElement("a");
        link.setAttribute("href", encodedUri);
        link.setAttribute("download", "graphData.csv");

        link.click();
    }


        return(
            <div>
                <div><button onClick={Download}>Download</button></div>
                <Table>
                    <tbody>
                       <tr><td>Id</td>
                        <td>WorkItem</td>
                        <td>Due Date</td>
                        <td>No Of Resources Needed</td>
                        <td>Actions</td>
                        </tr>
                        {AddItemReducerDisplay.map(data => (
                        <tr key={data.id}>
                             <td>{data.id}</td>
                             <td>{data.item}</td>
                             <td>{data.date}</td>
                             <td>{data.resources}</td>
                             <td><button onClick={() => {
                             onClickDeleteHire(this,data.id)
                             }}>DELETE</button></td>
                            </tr>
                    ))}
                    </tbody>
                </Table>
            </div>
        )  
}
export default Home
