import React, {useState} from 'react'
import {useDispatch,useSelector} from 'react-redux';
import {AddItemAction} from '../store/actions/index';


let id=1;
const AddItem = props => {

const dispatch = useDispatch();

  const [item, setItem] = useState("");
  const [cost,setCost] = useState("");
  const [quantity,setQuantity]=useState("")
  // const [lastName,setLastName] = useState("");
  // const [email,setEmail] = useState("");
  // const [password, setPassword] = useState("");
  // const [mobileNumber,setMobileNumber]=useState("");
//   const [response, setResponse] = useState("");

const AddItemReducer = useSelector(state=>state.AddItemReducer)
// console.log(RegisterData)



  const handleLogin = (evt) => {
      evt.preventDefault();
      // console.log(firstName);
      console.log(item);
      console.log(cost);
      console.log(quantity);
      // console.log(lastName);
      // console.log(email);
      // console.log(password);
      // console.log(mobileNumber);

        //  const firstName =firstName;
        //  const lastName =lastName;
        //   const email=email;
        //  const password=password;
        //   const mobileNumber=mobileNumber



    dispatch(AddItemAction(item,cost,quantity,id));

    // fetch('http://localhost:4000/backend/add', {
    //   method: 'POST',
    //   headers: {'Content-Type': 'application/json'},
    //   body: JSON.stringify({data})
    // }).then(function(response) {
    //   if(response.status >= 400) {
    //     throw new Error("Bad response from server");
    //   }
    //   return response.json();
    // }).then((data) =>{
    //   console.log(data);     
    //   if(data){

    //      props.history.push('/login');
    //   }   
    // }).catch(function(err) {
    //   console.log(err)
    // });

      setItem('');
      setCost('');
      setQuantity('')
      // setLastName('')
      // setPassword('');
      // setEmail('');
      // setPassword('');
      // setMobileNumber('');
   id++   
  }
  //  store.subscribe(()=>{console.log(store.getState())})
  return(
    <div>
    {/* <Navbar /> */}
    <center>
    <div className="loginMain">
      <div className="loginLogo">
        <div className="loginContent">

        </div>
      </div>
      <div className="registerContent">

      </div>
      <br></br>
      <div className="col-6 inputFields">
          <center>
        <form onSubmit={handleLogin}>
           <div className="">
              <label>
              <input className="form-control" type="text"
              value={item}
              onChange={e => setItem(e.target.value)}
              placeholder="Item" required/>
              </label>
            </div>
            <br></br>
            <div className="">
              <label>
              <input className="form-control" type="text"
              value={cost}
              onChange={e => setCost(e.target.value)}
              placeholder="Cost" required/>
              </label>
            </div>
            <br></br>
            <div className="">
              <label>
              <input className="form-control" type="text"
              value={quantity}
              onChange={e => setQuantity(e.target.value)}
              placeholder="Quantity" required/>
              </label>
            </div>

<br></br>
            <div className="inputFields">
            <center>  <input className="btn-sm login" type="submit" value="AddItem" /></center>
            </div>
        </form>
        </center>
      </div>
    </div>
    </center>

    </div>
  );
}
export default AddItem;
