import axios from 'axios';   
import {REGISTER} from './types';
import {useDispatch} from 'react-redux'


const apiUrl = 'http://localhost:4000/backend';

export const creaRegisterAction = ({ data }) => {
    const dispatch = useDispatch();
    return axios.post(`${apiUrl}/add`, {data})
      .then(response => {
        dispatch(RegisterAction(response.data))
      })
      .catch(error => {
        throw(error);
      });
  };



const action =(type,payload) => {
    return{type,payload}
}
export const RegisterAction =(data)=>action(REGISTER,
    {
    data
    })
