// import {REGISTER} from '../actions/types';
import {combineReducers} from 'redux';



const RegisterReducer = (data=[],action)=>{
    switch(action.type){
        case 'REGISTER':
            return [...data,action.payload]
            case 'DELETE':

                    return data.filter(data => data.id !== action.payload.id)
            default:
                return data
    }
}

export default combineReducers({
    RegisterReducer
})
