// import axios from 'axios'
import {ADDITEM,DELETE} from './types';

const action =(type,payload) =>{
    return{type,payload}
}
export const RegisterAction =(item,cost,id)=>
    action(REGISTER,
    {
    item,
    cost,
    id
}) 
export const deleteAction = id => action(DELETE, id)
